import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../contexts/GameContext';
import { Play, BookOpen, Edit3, HelpCircle, Clock, Target } from 'lucide-react';
import styled from 'styled-components';

const GameSelectionContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  color: white;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const Subtitle = styled.p`
  font-size: 1.1rem;
  opacity: 0.9;
`;

const GameGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.5rem;
  width: 100%;
  max-width: 500px;
`;

const GameCard = styled(motion.div)`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  cursor: pointer;
  border: 3px solid transparent;
  transition: all 0.3s ease;

  &:hover {
    border-color: #667eea;
    transform: translateY(-5px);
  }
`;

const GameIcon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: ${props => props.bgColor};
  margin-bottom: 1rem;
  color: white;
`;

const GameTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
  color: #333;
`;

const GameDescription = styled.p`
  color: #666;
  margin-bottom: 1rem;
  line-height: 1.5;
`;

const GameFeatures = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
`;

const FeatureTag = styled.span`
  background: #f0f0f0;
  color: #666;
  padding: 0.25rem 0.75rem;
  border-radius: 15px;
  font-size: 0.8rem;
`;

const PlayButton = styled(motion.button)`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 25px;
  font-weight: bold;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  width: 100%;
  justify-content: center;
  font-size: 1rem;

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const GameConfigModal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0,0,0,0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  width: 100%;
  max-width: 400px;
  max-height: 80vh;
  overflow-y: auto;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 1.5rem;
  color: #333;
  text-align: center;
`;

const ConfigGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const ConfigLabel = styled.label`
  display: block;
  font-weight: bold;
  margin-bottom: 0.5rem;
  color: #333;
`;

const ConfigSelect = styled.select`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e0e0e0;
  border-radius: 10px;
  font-size: 1rem;
  background: white;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const ConfigInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e0e0e0;
  border-radius: 10px;
  font-size: 1rem;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 2rem;
`;

const Button = styled.button`
  flex: 1;
  padding: 0.75rem;
  border-radius: 10px;
  font-weight: bold;
  cursor: pointer;
  border: none;
  font-size: 1rem;

  &.primary {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
  }

  &.secondary {
    background: #f0f0f0;
    color: #666;
  }
`;

const games = [
  {
    id: 'match',
    title: 'Match the Following',
    description: 'Match Tamil words with their English meanings or vice versa.',
    icon: <BookOpen size={30} />,
    bgColor: '#4CAF50',
    features: ['Quick Learning', 'Visual Matching', 'Bilingual'],
  },
  {
    id: 'fill_blanks',
    title: 'Fill in the Blanks',
    description: 'Complete sentences by filling in missing Tamil words or English meanings.',
    icon: <Edit3 size={30} />,
    bgColor: '#FF9800',
    features: ['Context Learning', 'Sentence Structure', 'Comprehensive'],
  },
  {
    id: 'mcq',
    title: 'Multiple Choice',
    description: 'Choose the correct English meaning for Tamil words from multiple options.',
    icon: <HelpCircle size={30} />,
    bgColor: '#2196F3',
    features: ['Quick Selection', 'Multiple Options', 'Time-based'],
  },
];

const GameSelection = () => {
  const navigate = useNavigate();
  const { startGame, loading } = useGame();
  const [selectedGame, setSelectedGame] = useState(null);
  const [gameConfig, setGameConfig] = useState({
    totalQuestions: 10,
    domain: '',
    difficulty: 'Mixed',
  });

  const handleGameSelect = (gameId) => {
    setSelectedGame(gameId);
  };

  const handleStartGame = async () => {
    try {
      await startGame({
        gameType: selectedGame,
        ...gameConfig,
      });
      navigate('/game');
    } catch (error) {
      console.error('Failed to start game:', error);
    }
  };

  const handleConfigChange = (field, value) => {
    setGameConfig(prev => ({
      ...prev,
      [field]: value,
    }));
  };

  return (
    <GameSelectionContainer>
      <Header>
        <Title>தமிழ் வார்த்தை விளையாட்டு</Title>
        <Subtitle>Tamil Word Learning Games</Subtitle>
      </Header>

      <GameGrid>
        {games.map((game) => (
          <GameCard
            key={game.id}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => handleGameSelect(game.id)}
          >
            <GameIcon bgColor={game.bgColor}>
              {game.icon}
            </GameIcon>
            <GameTitle>{game.title}</GameTitle>
            <GameDescription>{game.description}</GameDescription>
            <GameFeatures>
              {game.features.map((feature, index) => (
                <FeatureTag key={index}>{feature}</FeatureTag>
              ))}
            </GameFeatures>
            <PlayButton
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={(e) => {
                e.stopPropagation();
                handleGameSelect(game.id);
              }}
            >
              <Play size={20} />
              Play Game
            </PlayButton>
          </GameCard>
        ))}
      </GameGrid>

      {selectedGame && (
        <GameConfigModal
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={() => setSelectedGame(null)}
        >
          <ModalContent
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            onClick={(e) => e.stopPropagation()}
          >
            <ModalTitle>Game Configuration</ModalTitle>
            
            <ConfigGroup>
              <ConfigLabel>Number of Questions</ConfigLabel>
              <ConfigInput
                type="number"
                min="5"
                max="20"
                value={gameConfig.totalQuestions}
                onChange={(e) => handleConfigChange('totalQuestions', parseInt(e.target.value))}
              />
            </ConfigGroup>

            <ConfigGroup>
              <ConfigLabel>Domain (Optional)</ConfigLabel>
              <ConfigSelect
                value={gameConfig.domain}
                onChange={(e) => handleConfigChange('domain', e.target.value)}
              >
                <option value="">All Domains</option>
                <option value="Time">Time</option>
                <option value="Nature">Nature</option>
                <option value="Culture">Culture</option>
                <option value="Architecture">Architecture</option>
                <option value="Measurement">Measurement</option>
                <option value="Food">Food</option>
                <option value="Clothing">Clothing</option>
                <option value="Other">Other</option>
              </ConfigSelect>
            </ConfigGroup>

            <ConfigGroup>
              <ConfigLabel>Difficulty</ConfigLabel>
              <ConfigSelect
                value={gameConfig.difficulty}
                onChange={(e) => handleConfigChange('difficulty', e.target.value)}
              >
                <option value="Mixed">Mixed</option>
                <option value="Easy">Easy</option>
                <option value="Medium">Medium</option>
                <option value="Hard">Hard</option>
              </ConfigSelect>
            </ConfigGroup>

            <ButtonGroup>
              <Button
                className="secondary"
                onClick={() => setSelectedGame(null)}
              >
                Cancel
              </Button>
              <Button
                className="primary"
                onClick={handleStartGame}
                disabled={loading}
              >
                {loading ? 'Starting...' : 'Start Game'}
              </Button>
            </ButtonGroup>
          </ModalContent>
        </GameConfigModal>
      )}
    </GameSelectionContainer>
  );
};

export default GameSelection;
